            checks:[],
            checkArgs:[],
            checkTips:[],
        };
        scope.addOptions=function (){
            const unwatch = Atom.watchChange(scope,'use',function (ov,nv,obj){
                switch(nv){
                    case 'cc-options':
                    scope.exp='u.name as u for u in users';
                    scope.optStr='';
                    break;
                    case 'cc-repeat':
                    scope.exp='u in users';
                    scope.optStr='{{u.name}}';
                    break;
                    case 'cc-for':
                    scope.exp='u in users';
                    scope.optStr='{{u.name}}';
                    break;
                }
            })
            $modal.dialog()
            .content(app.getPaths('views/modal/selectOption.atom'))
            .scope(scope)
            .width(555)
            .height('auto')
            .ok(function(){
                unwatch();
            })
            .cancel(function (){
                unwatch();
                scope.exp='';
                scope.use='';
                scope.optStr='';
            });
        };
