div.script:
const target = $event.target;
const sd = target.shadowRoot||target;
var form = sd.findOne('form');
if(!form){
    form = ce('form');
    sd.append(form);
}
switch(target.localName){
    case 'cc-form':
        const nds = Atom.toNodes(body);
        const list = [...nds];
        form.append(...nds);
        undoList.push(function (){
            list.forEach(nd=>nd.remove());
            list.length=0;
        });
    break;
}
